import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';

function Navbar() {
  return (
    <nav className="navbar">
      <div className="navbar-title">ðŸš€ Crypto Dashboard</div>
      <div className="navbar-links">
        <a href="#" className="active">Dashboard</a>
        <a href="#">History</a>
        <a href="#">About</a>
      </div>
    </nav>
  );
}

function Footer() {
  return (
    <footer className="footer">
      <span>Â© {new Date().getFullYear()} Crypto Dashboard. Powered by CoinGecko API.</span>
    </footer>
  );
}

function App() {
  const [coins, setCoins] = useState([]);
  const [search, setSearch] = useState('');
  const [sortKey, setSortKey] = useState('marketCap');
  const [sortOrder, setSortOrder] = useState('desc');
  const [activeRow, setActiveRow] = useState(null);

  const fetchCoins = async () => {
    const { data } = await axios.get('http://localhost:5000/api/coins');
    setCoins(data);
  };

  useEffect(() => {
    fetchCoins();
    const interval = setInterval(fetchCoins, 30 * 60 * 1000); // 30 min
    return () => clearInterval(interval);
  }, []);

  const filtered = coins.filter(coin =>
    coin.name.toLowerCase().includes(search.toLowerCase()) ||
    coin.symbol.toLowerCase().includes(search.toLowerCase())
  );

  const sorted = [...filtered].sort((a, b) => {
    if (sortOrder === 'asc') {
      return a[sortKey] > b[sortKey] ? 1 : -1;
    } else {
      return a[sortKey] < b[sortKey] ? 1 : -1;
    }
  });

  const handleSort = (key) => {
    if (sortKey === key) {
      setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      setSortKey(key);
      setSortOrder('desc');
    }
  };

  return (
    <>
      <Navbar />
      <div className="container">
        <h1>Top 10 Cryptocurrencies</h1>
        <input
          type="text"
          placeholder="Search by name or symbol..."
          value={search}
          onChange={e => setSearch(e.target.value)}
          className="search"
        />
        <table>
          <thead>
            <tr>
              <th onClick={() => handleSort('name')} className={sortKey === 'name' ? 'active' : ''}>
                Name
                {sortKey === 'name' && (
                  <span className="sort-arrow">{sortOrder === 'asc' ? 'â–²' : 'â–¼'}</span>
                )}
              </th>
              <th onClick={() => handleSort('symbol')} className={sortKey === 'symbol' ? 'active' : ''}>
                Symbol
                {sortKey === 'symbol' && (
                  <span className="sort-arrow">{sortOrder === 'asc' ? 'â–²' : 'â–¼'}</span>
                )}
              </th>
              <th onClick={() => handleSort('price')} className={sortKey === 'price' ? 'active' : ''}>
                Price (USD)
                {sortKey === 'price' && (
                  <span className="sort-arrow">{sortOrder === 'asc' ? 'â–²' : 'â–¼'}</span>
                )}
              </th>
              <th onClick={() => handleSort('marketCap')} className={sortKey === 'marketCap' ? 'active' : ''}>
                Market Cap
                {sortKey === 'marketCap' && (
                  <span className="sort-arrow">{sortOrder === 'asc' ? 'â–²' : 'â–¼'}</span>
                )}
              </th>
              <th onClick={() => handleSort('change24h')} className={sortKey === 'change24h' ? 'active' : ''}>
                24h % Change
                {sortKey === 'change24h' && (
                  <span className="sort-arrow">{sortOrder === 'asc' ? 'â–²' : 'â–¼'}</span>
                )}
              </th>
              <th onClick={() => handleSort('lastUpdated')} className={sortKey === 'lastUpdated' ? 'active' : ''}>
                Last Updated
                {sortKey === 'lastUpdated' && (
                  <span className="sort-arrow">{sortOrder === 'asc' ? 'â–²' : 'â–¼'}</span>
                )}
              </th>
            </tr>
          </thead>
          <tbody>
            {sorted.map((coin, idx) => (
              <tr
                key={coin.coinId}
                className={activeRow === idx ? 'active-row' : ''}
                onMouseDown={() => setActiveRow(idx)}
                onMouseUp={() => setActiveRow(null)}
                style={{ cursor: 'pointer', transition: 'background 0.2s' }}
              >
                <td>{coin.name}</td>
                <td>{coin.symbol.toUpperCase()}</td>
                <td>${coin.price.toLocaleString()}</td>
                <td>${coin.marketCap.toLocaleString()}</td>
                <td className={coin.change24h > 0 ? 'green' : 'red'}>
                  {coin.change24h?.toFixed(2)}%
                </td>
                <td>{new Date(coin.lastUpdated).toLocaleString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <Footer />
    </>
  );
}

export default App;
